var path = require('path');

var Cache = new (function() {
	var self = this;
	self._cache = {};
	
	self.has = function(cacheKey) {
		return self._cache.hasOwnProperty(cacheKey);
	}
	
	self.set = function(cacheKey, data) {
		self._cache[cacheKey] = data;
	}
	
	self.get = function(cacheKey) {
		return self._cache[cacheKey];
	}
	
	self.clear = function(cacheKey) {
		delete self._cache[cacheKey];
	}
	
	self.clearMatches = function(regex) {
		var i;
		for(i in self._cache) {
			if(regex.test(i)) {
				self.clear(i);
			}
		}
	}
})();

module.exports = Cache;