var path = require('path');

var Cache = new (function() {
	var self = this;
	self._cache = {};
	
	self.hasFile = function(filePath) {
		return self._cache.hasOwnProperty(filePath);
	}
	
	self.setFile = function(filePath, data) {
		self._cache[filePath] = data;
	}
	
	self.getFile = function(filePath) {
		return self._cache[filePath];
	}
})();

module.exports = Cache;