var io = require('socket.io/node_modules/socket.io-client/lib/socket.io-client'),
	conf = require('ncombo/core/configmanager'),
	ws = require('ncombo/core/webservice');

var config = conf.parseConfig(__dirname + '/../../config.node.json');

var ServerService = new (function() {
	var self = this;
	
	self.getClient = function(port) {
		var host = 'localhost';
		var secure = false;
		var wsEndPoint = config.webServiceEndpoint;
		return ws.getClient(host, port, secure, wsEndPoint);
	}
})();

module.exports = ServerService;