var fs = require('fs'),
	path = require('path'),
	cache = require('ncombo/cache');

var Responder = new (function() {
	var self = this;
	
	self.run = function(req, res, next) {
		if(req.rout.buffer) {
			res.writeHead(200);
			res.end(req.rout.buffer);
		} else {
			res.writeHead(200);
			req.rout.getLastStream().pipe(res);
		}
	}
})();

module.exports = Responder;
